import React, { useState, useEffect } from "react";
import WeatherCard from "./WeatherCard";
import WbSunnyIcon from "@mui/icons-material/WbSunny";
import WbCloudyIcon from "@mui/icons-material/WbCloudy";
import LensBlurIcon from "@mui/icons-material/LensBlur";

const WeatherInfo = (props) => {
  // const [searchValue, setSearchValue] = useState("Thailand");
  const [tempInfo, setTempInfo] = useState({});
  const [IsLoading, setIsLoading] = useState("");
  const [error, setError] = useState("");
  const [weathertheState, setWeathertheState] = useState("");
  const [temp, setTemp] = useState("");
  const [WeatherMood, setWeatherMood] = useState();

  const getWeatherInfo = async () => {
    const fetchUrl = async (url) => {
      setIsLoading(true);
      setError(null);

      const handleTemp = (event) => {
        props.setTemp(event.target.value);
        console.log(event.target.value);
      };

      const handleWeathermood = (event) => {
        setWeatherMood(event.target.value);
        console.log(event.target.value);
      };

      const handleCountryName = (event) => {
        setCheckOut(event.target.value);
        console.log(event.target.value);
      };

      try {
        let url = `https://api.openweathermap.org/data/2.5/weather?q=${props.searchValue}&units=metric&appid=80be835590c51d8786ce59810daef2be`;

        const res = await fetch(url);
        const data = await res.json();
        console.log(data);
        const { temp } = data.main;
        const { main: weathermood } = data.weather[0];
        const { name } = data;

        const myNewWeatherInfo = {
          temp,
          weathermood,
          name,
        };

        setTempInfo(myNewWeatherInfo);
      } catch (error) {
        console.log(error);
        setIsLoading(false);
      }
    };

    // useEffect(() => {
    //   fetchUrl();
    // }, []);

    // useEffect(() => {
    //   fetchData();
    // }, [people]);

    // if (weathermood)
    //   switch (weathermood) {
    //     case "clouds":
    //       setWeathertheState("WbCloudyIcon");
    //       break;
    //     case "haze":
    //       setWeathertheState("LensBlurIcon");
    //       break;
    //     case "clear":
    //       setWeathertheState("WbSunnyIcon");
    //       break;
    //   }
  };

  getWeatherInfo();

  // getWeatherInfo({}, [weathermood]);

  return (
    <div>
      <div className="wrap">
        <div className="search">
          <h3> weather check</h3>
          <input
            type="search"
            autoFocus
            id="search"
            className="searchTerm"
            placeholder="search..."
            value={props.name}
            onChange={(e) => props.setSearchValue(e.target.value)}
          />
          <button
            className="searchButton"
            type="button"
            onClick={getWeatherInfo}
          >
            check
          </button>
        </div>
      </div>

      <div className="weatherInfo">
        <div className="temperature">
          <span>
            Weather for {tempInfo[3]} is {tempInfo[1]}&deg;
          </span>
        </div>
        <div className="description">
          <div className="weatherCondition">{tempInfo[2]}</div>
        </div>
      </div>
      {/* <WeatherCard tempInfo={tempInfo} /> */}
    </div>
  );
};

export default WeatherInfo;
