import React, { useState } from "react";
import WeatherCard from "./WeatherCard";

const PageThree = (props) => {
  const [searchValue, setSearchValue] = useState("Thailand");
  const [tempInfo, setTempInfo] = useState({});

  const getWeatherInfo = async () => {
    try {
      let url = `https://api.openweathermap.org/data/2.5/weather?q=${props.name}&units=metric&appid=80be835590c51d8786ce59810daef2be`;

      const res = await fetch(url);
      const data = await res.json();
      console.log(data);
      const { temp } = data.main;
      const { main: weathermood } = data.weather[0];
      const { name } = data;

      const myNewWeatherInfo = {
        temp,
        weathermood,
        name,
      };

      setTempInfo(myNewWeatherInfo);
    } catch (error) {
      console.log(error);
    }
  };

  // useEffect(() => {});
  // if (weathermood)
  //   switch(weathermood){
  //     case"clouds":
  //       setWeatherState("wi-day-cloudy")
  //       break;
  //     case"haze":
  //      setWeathertheState("wi-fog");
  //      break;
  //      case"clear"
  // setWeathertheState("wi-day-sunny")
  // break;
  //   }
  // }
  //   getWeatherInfo();
  // }, [weathermood]);

  return (
    <div>
      <div className="wrap">
        <div className="search">
          <h3> Weather here...</h3>
          <input
            type="search"
            autoFocus
            id="search"
            className="searchTerm"
            placeholder="search..."
            value={props.name}
            onChange={props.handleChangforWeather}
          />
          <button
            className="searchButton"
            type="submit"
            onClick={props.getWeatherInfo}
          >
            check
          </button>
          Weather for {props.tempInfo[3]} is {props.tempInfo[1]}&deg;
          <div className="weatherCondition">{props.tempInfo[2]}</div>
        </div>
      </div>
      {/* <WeatherCard tempInfo={tempInfo} /> */}
    </div>
  );
};

export default PageThree;
