import React, { useState, useEffect } from "react";

const CountryImage = () => {
  const [endPoint, setEndPoint] = useState("");
  const [container, setContainer] = useState([]);
  const [finalPoint, setFinalPoint] = useState("");

  const fetchMe = {
    method: "GET",
    headers: {
      "X-RapidAPI-Key": "3dc1a90a57msh1832834cdf55bdep1ed1b2jsnc624f5031a4f",
      "X-RapidAPI-Host": "bing-image-search1.p.rapidapi.com",
    },
  };

  useEffect(() => {
    fetchMe();
  }, [endPoint]);

  fetch("https://bing-image-search1.p.rapidapi.com/images/trending", endPoint)
    .then((response) => response.json())
    .then((response) => console.log(response))
    .then((data) => setContainer(data.categories))
    .catch((err) => console.error(err));

  useEffect(() => {
    fetchMe();
  }, [finalPoint]);

  const handleChanges = (e) => {
    setEndPoint(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFinalPoint(endPoint);
  };

  return (
    <div className="CountryImage">
      <form onSubmit={handleSubmit}>
        <input type="text" value={endPoint} onChange={handleChanges}></input>
        <button type="submit">submit</button>
      </form>
      {container.map((item) => {
        return <p>{item}</p>;
      })}
    </div>
  );
};
export default CountryImage;
