import React, { useState, useEffect } from "react";

const CountryImage = () => {
  const [endPoint, setEndPoint] = useState("");
  const [container, setContainer] = useState([]);
  useEffect(() => {
    fetchMe();
  }, [endPoint]);

  const fetchMe = () => {
    fetch(
      `https://bing-image-search1.p.rapidapi.com/images/trending=+${endPoint}`,
      {
        method: "GET",
        headers: {
          "X-RapidAPI-Key":
            "3dc1a90a57msh1832834cdf55bdep1ed1b2jsnc624f5031a4f",
          "X-RapidAPI-Host": "bing-image-search1.p.rapidapi.com",
        },
      }
    )
      .then((response) => {
        console.log(response.json());
      })

      .then((data) => {
        setContainer(data.categories);
      })

      .catch((err) => {
        console.error(err);
      });
  };
  const handleChanges = (e) => {
    setEndPoint(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className="CountryImage">
      <form onSubmit={handleSubmit}>
        <input type="text" value={endPoint} onChange={handleChanges}></input>
        <button type="submit">submit</button>
      </form>
      {container.map((item) => {
        return <p>{item.image}</p>;
      })}
    </div>
  );
};

export default CountryImage;
