import React, { useState } from "react";
import PageTwo from "../component1/PageTwo";
import CompletedForm from "./CompletedForm";
import Form from "./Form";

const WishList = (props) => {
  // const [name, setName] = useState("");
  const [checkIn, setCheckIn] = useState("");
  const [checkOut, setCheckOut] = useState("");
  const [formError, setFormError] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [updateList, setUpdateList] = useState("");
  const [hasSubmited, setHasSubmited] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    const newWishList = { checkIn, checkOut };
    setUpdateList([props.name, checkIn, checkOut]);
    setHasSubmited(true);

    // console.log(props.name, checkIn, checkOut);
    props.handleClick(newWishList);
    setFormError(newWishList);
    setIsSubmit(true);

    props.setName("");
    setCheckIn("");
    setCheckOut("");
  };

  const addToWishList = (item) => {
    setWishList([...wishList, item]);
  };
  const addToSaveFolder = (item) => {
    setSaveFolder([...saveFolder, item]);
  };

  const handleNewDestination = (event) => {
    props.setName(event.target.value);
    console.log(event.target.value);
  };

  const handleNewcCheckIn = (event) => {
    setCheckIn(event.target.value);
    console.log(event.target.value);
  };

  const handleNewCheckOut = (event) => {
    setCheckOut(event.target.value);
    console.log(event.target.value);
  };

  const handleClearButton = () => {
    setHasSubmited(false);
  };

  const handleAddWishList = () => {};
  return (
    <div>
      {hasSubmited ? (
        <CompletedForm updateList={updateList} />
      ) : (
        <Form
          handleSubmit={handleSubmit}
          handleNewDestination={handleNewDestination}
          handleNewcCheckIn={handleNewcCheckIn}
          handleNewCheckOut={handleNewCheckOut}
        />
      )}
      <button onClick={handleClearButton}>clear</button>
      <button onClick={handleAddWishList}>add to wishlist</button>
      <AllWishLists wishList={wishList} addToSaveFolder={addToSaveFolder} />
    </div>
  );
};
export default WishList;
