const Country = () => {
  const [user, setUser] = useState([]);

  const fetchData = () => {
    fetch("https://randomuser.me/api/")
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        let people = data.results;
        setUser(people);
        console.log(people);
      });
  };
