import React, { useState, useEffect } from "react";
import WeatherCard from "../weather/WeatherCard";

const PageThree = () => {
  const [searchValue, setSearchValue] = useState("Singapore");
  const [tempInfo, setTempInfo] = useState({});

  const getWeatherInfo = async () => {
    try {
      let url = `https://api.openweathermap.org/data/2.5/weather?q=${searchValue}&units=metric&appid=80be835590c51d8786ce59810daef2be`;

      const res = await fetch(url);
      const data = await res.json();
      console.log(data);
      const { temp } = data.main;
      const { main: weathermood } = data.weather[0];
      const { name } = data;

      const myNewWeatherInfo = {
        temp,
        weathermood,
        name,
      };

      setTempInfo(myNewWeatherInfo);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {});
  // if (weathermood)
  //   switch(weathermood){
  //     case"clouds":
  //       setWeatherState("wi-day-cloudy")
  //       break;
  //     case"haze":
  //      setWeathertheState("wi-fog");
  //      break;
  //      case"clear"
  // setWeathertheState("wi-day-sunny")
  // break;
  //   }
  // }
  //   getWeatherInfo();
  // }, [weathermood]);

  return (
    <div>
      <div className="wrap">
        <div className="search">
          <h7> check the weather forecast here</h7>
          <input
            type="search"
            autoFocus
            id="search"
            className="searchTerm"
            placeholder="search..."
            value={searchValue}
            onChange={(e) => setSearchValue(e.target.value)}
          />
          <button
            className="searchButton"
            type="button"
            onClick={getWeatherInfo}
          >
            check
          </button>
        </div>
      </div>
      <WeatherCard tempInfo={tempInfo} />
    </div>
  );
};

export default PageThree;

