import React, { useState } from "react";

const Search = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [input, setInput] = useState("");
  const [gifUrl, setGifUrl] = useState("");
  // const [showRandom, setShowRandom] = useState(true);
  // const [printWord, setPrintWord] = useState("Hello State");

  const fetchGif = async (url) => {
    setIsLoading(true);
    setError(null);

    try {
      const res = await fetch(url);
      if (res.status !== 200) {
        throw new Error("Something went wrong.");
      }

      const data = await res.json();

      setGifUrl(data);
      console.log(data.names);
    } catch (err) {
      setError(err.message);
    }

    setIsLoading(false);
  };

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  // Does not work, can define const url by itself.
  //   const getGifWithInput = () => {
  //     const url =
  //       "https://api.giphy.com/v1/gifs/search?api_key=G2aSfGklbIhABGgUtipWmH5eVI6GyMCC&q=" +
  //       input;
  //   };

  const handleSubmit = (event) => {
    event.preventDefault();
    // setShowRandom(false); here 1
    const url = "https://travelbriefing.org/Netherlands?format=json" + input;

    fetchGif(url);
    setInput("");
  };

  ////////////////////////////////////////////////////////
  let content = "";
  if (gifUrl) {
    content = <h1 src={gifUrl} alt="Searched Gif"></h1>;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>The searched gif is loading .. please wait</p>;
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label htmlFor="input">Where do u want to go next </label>

        <input
          id="input"
          value={input}
          type="text"
          onChange={handleInputChange}
          placeholder="Search For a Gif"
        />

        <button type="submit">search</button>
      </form>

      <div className="col-sm-4">{content}</div>
    </>
  );
};

export default Search;
