import React, { useState } from "react";
import CompletedForm from "./component2/CompletedForm";

const WishList = (props) => {
  const [name, setName] = useState("");
  const [checkIn, setCheckIn] = useState("");
  const [checkOut, setCheckOut] = useState("");
  const [formError, setFormError] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [updateList, setUpdateList] = useState("");
  const [hasSubmited, setHasSubmited] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    const newWishList = { name, checkIn, checkOut };
    setUpdateList([name, checkIn, checkOut]);
    setHasSubmited(true);

    console.log(name, checkIn, checkOut);
    props.handleClick(newWishList);
    setFormError(newWishList);
    setIsSubmit(true);

    setName("");
    setCheckIn("");
    setCheckOut("");
  };

  const handleNewDestination = (event) => {
    setName(event.target.value);
    console.log(event.target.value);
  };

  const handleNewcCheckIn = (event) => {
    setCheckIn(event.target.value);
    console.log(event.target.value);
  };

  const handleNewCheckOut = (event) => {
    setCheckOut(event.target.value);
    console.log(event.target.value);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="search">
          <div className="container">
            <label htmlFor="">where you'd like to go</label>
            <br />

            <input
              type="text"
              placeholder="      Location..."
              className="input"
              value={name}
              onChange={handleNewDestination}
            />
          </div>
        </div>
        <div className="search">
          <div className="container">
            <label htmlFor="">Check in </label>
            <input
              type="date"
              className="CheckIn"
              value={checkIn}
              onChange={handleNewcCheckIn}
            />
          </div>
        </div>
        <div className="search">
          <div className="container">
            <label htmlFor="">Check out</label>
            <input
              type="date"
              className="CheckIn"
              value={checkOut}
              onChange={handleNewCheckOut}
            />
          </div>
          <button className="CheckIn" type="submit">
            Add to wishlist
          </button>
          {/* <h4>You next destination is {updateList[0]}</h4>
          <h4>C/I date {updateList[1]}</h4>
          <h4>C/O date {updateList[2]}</h4> */}
        </div>
      </form>
      {hasSubmited ? (
        <Form
          handleSubmit={handleSubmit}
          handleNewDestination={handleNewDestination}
          handleNewCheckOut={handleNewCheckOut}
        />
      ) : (
        <CompletedForm updateList={updateList} />
      )}
    </div>
  );
};
export default WishList;
