import React, { useState } from "react";
import { Link, Route, Routes, Navigate, NavLink } from "react-router-dom";

import image2 from "./images/img2.jpg";
import NavBar from "./component1/NavBar";
import PageOne from "./component1/PageOne";
import PageTwo from "./component1/PageTwo";
import PageThree from "./component1/PageThree";
import PageFour from "./component1/PageFour";
import WishList from "./component2/WishList";
import WeatherInfo from "./component1/WeatherInfo";
// import AllWishLists from "./component1/AllWishLists";

function App() {
  const [wishList, setWishList] = useState([]);
  const [saveFolder, setSaveFolder] = useState([]);
  const [name, setName] = useState("");
  const [img, setImg] = useState("");
  const [searchValue, setSearchValue] = useState("Thailand");
  const [tempInfo, setTempInfo] = useState({});
  const [IsLoading, setIsLoading] = useState("");
  const [error, setError] = useState("");
  const [weathertheState, setWeathertheState] = useState("");
  const [removeList, setRemoveList] = useState("");

  const addToWishList = (item) => {
    setWishList([...wishList, item]);
  };
  console.log(wishList);

  const handleInput = (e) => {
    const data = e.target.value;
    setImg(data);
  };

  const handleChangforWeather = (e) => {
    setSearchValue(e.target.value);
  };

  const handleRemove = (index) => {
    const newList = wishList.filter((d, i) => i !== index);
    setRemoveList(newList);
    console.log(newList);
    console.log("hey");
  };

  let things = wishList.map((d, i) => {
    return (
      <li key={i} onClick={() => handleRemove(i)}>
        name: {d.name}- Check in :{d.checkIn} - Check Out:
        {d.checkOut}
        {/* <button
          onClick={() => {
            handleRemove(i);
          }}
        > */}
        {/* remove */}
        {/* </button> */}
      </li>
    );
  });

  return (
    <div>
      <div className="brand">
        {/* <img src={image} className="logo" /> */}
        <h2 className="All">All in one travel app </h2>
      </div>
      <h4 className="time">{new Date().toLocaleString()}</h4>
      {/* <div> */}
      <div className="title">
        <img src={image2} className="img2"></img>
      </div>
      <div className="moveup">
        <h1 className="travel">
          TRAVEL <span className="To">To</span> EXPLORE
        </h1>

        <WishList
          addToWishList={addToWishList}
          name={name}
          setName={setName}
          handleInput={handleInput}
        />

        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>

        <NavBar />
        <Routes>
          <Route path="/" element={<Navigate replace to="/page-one" />} />
          <Route path="/page-one" element={<PageOne />} />
          <Route path="/page-two" element={<PageTwo name={name} />} />
          <Route
            path="/page-three"
            element={
              <PageThree
                setSearchValue={setSearchValue}
                searchValue={searchValue}
                name={name}
                handleChangforWeather={handleChangforWeather}
                tempInfo={tempInfo}
              />
            }
          />
          <Route
            path="/page-four"
            element={<PageFour things={things} removeList={removeList} />}
          />
        </Routes>
      </div>
    </div>
  );
}

export default App;
