import React, { useState, useEffect } from "react";

const WeatherInfo = () => {
  const [searchValue, setSearchValue] = useState("Singapore");

  const getweatherInfo = async () => {
    try {
      let url = `https://api.openweathermap.org/data/2.5/weather?q=${searchValue}&appid=80be835590c51d8786ce59810daef2be`;

      const res = await fetch(url);
      const data = await res.json();

      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    getweatherInfo();
  }, []);

  return (
    <div>
      <div className="wrap">
        <div className="search">
          <input
            type="search"
            autoFocus
            id="search"
            className="searchTerm"
            placeholder="search..."
            value={searchValue}
            onChange={(e) => setSearchValue(e.target.value)}
          />
          <button
            className="searchButton"
            type="button"
            onClick={getweatherInfo}
          >
            ok
          </button>
        </div>
      </div>
      <article className="widge">
        <div className="weatherIcon">
          <i className="wi wi day sunny"></i>
        </div>
        <div className="weatherInfo">
          <div className="temperature">
            <span>25.5&deg;</span>
          </div>
          <div className="description">
            <div className="weatherCondition">sunny</div>
            <div className="place">Singapore</div>
          </div>
        </div>
        <div className="date">{new Date().toLocaleString()}</div>
      </article>
    </div>
  );
};

export default WeatherInfo;

