import React, { useState } from "react";
import Random from "./Random";
import Button from "./Button";

const Search = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [input, setInput] = useState("");
  const [gifUrl, setGifUrl] = useState("");
  const [showRandom, setShowRandom] = useState(true);
  const [printWord, setPrintWord] = useState("Hello State");

  const fetchGif = async (url) => {
    setIsLoading(true);
    setError(null);

    try {
      const res = await fetch(url);
      if (res.status !== 200) {
        throw new Error("Something went wrong.");
      }

      const data = await res.json();
      console.log(data);
      setGifUrl(data.data[0].images.downsized.url);
    } catch (err) {
      setError(err.message);
    }

    setIsLoading(false);
  };

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  // Does not work, can define const url by itself.
  //   const getGifWithInput = () => {
  //     const url =
  //       "https://api.giphy.com/v1/gifs/search?api_key=G2aSfGklbIhABGgUtipWmH5eVI6GyMCC&q=" +
  //       input;
  //   };

  const handleSubmit = (event) => {
    event.preventDefault();
    // setShowRandom(false); here 1
    const url =
      "https://api.giphy.com/v1/gifs/search?api_key=G2aSfGklbIhABGgUtipWmH5eVI6GyMCC&q=" +
      input;

    fetchGif(url);
    setInput("");
  };

  ////////////////////////////////////////////////////////
  let content = "";
  if (gifUrl) {
    content = <img src={gifUrl} alt="Searched Gif"></img>;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>The searched gif is loading .. please wait</p>;
  }

  // const handleClickBackToRandomGif = () => {
  //   setShowRandom(true);
  // };

  const handleBtnforNewGif = () => {
    setPrintWord("ChangeToGina");
  };

  return (
    <>
      <br />
      <div className="row">
        <div className="col-sm-2"></div>
        <div className="col-sm-4">
          <h2>Component7</h2>
        </div>
        <div className="col-sm-6"></div>
      </div>
      <br />
      <form onSubmit={handleSubmit}>
        <div className="row">
          <div className="col-sm-2"></div>
          <div className="col-sm-1">
            <label htmlFor="input">Name: </label>
          </div>
          <div className="col-sm-3">
            <input
              id="input"
              value={input}
              type="text"
              onChange={handleInputChange}
              placeholder="Search For a Gif"
            />
          </div>
          <div className="col-sm-6">
            <button type="submit"></button>
          </div>
        </div>
      </form>
      <br />
      <div className="row">
        <div className="col-sm-2"></div>
        <div className="col-sm-4">{content}</div>
        <div className="col-sm-6"></div>
      </div>
      <br />
      <div className="row">
        <div className="col-sm-2"></div>
        <div className="col-sm-4">
          {/* <button onClick={handleClickBackToRandomGif}> */}
          {/* if put setState in onClick, need to put in a function, or it will keep looping */}
          Click to go back to Random Gif
          {/* </button> */}
          <button onClick={handleBtnforNewGif}>Click Me to change name</button>
          <h1>{printWord}</h1>
        </div>
        <div className="col-sm-6"></div>
      </div>
    </>
  );
};
// {showRandom ? <Random /> : content}
export default Search;
