import React from "react";
import WbSunnyIcon from "@mui/icons-material/WbSunny";
import WbCloudyIcon from "@mui/icons-material/WbCloudy";
import LensBlurIcon from "@mui/icons-material/LensBlur";

const WeatherCard = ({ tempInfo }) => {
  const { temp, weathermood, name } = tempInfo;
  const [weathertheState, setWeathertheState] = useState("");

  weathermood = weathertheState;

  if (weathermood)
    switch (weathermood) {
      case "clouds":
        setWeathertheState(WbSunnyIcon);
        break;
      case "haze":
        setWeathertheState(WbCloudyIcon);
        break;
      case "clear":
        setWeathertheState(LensBlurIcon);
        break;
    }

  return (
    <div>
      <article className="widge">
        <div className="weatherIcon">
          <i className="wi wi day sunny"></i>
        </div>
        <div className="weatherInfo">
          <div className="temperature">
            <span>
              Weather for {name} is {temp}&deg;
            </span>
          </div>
          <div className="description">
            <div className="weatherCondition">{weathermood}</div>
            {/* <div className="place"> {name}</div> */}
          </div>
        </div>
        <div className="date">Today is {new Date().toLocaleString()}</div>
      </article>
    </div>
  );
};

export default WeatherCard;
